SELECT ?movie ?title ?name
WHERE {
  ?movie dbpedia-owl:starring [ rdfs:label "Brad Pitt"@en ];
         rdfs:label ?title;
         dbpedia-owl:director [ rdfs:label ?name ].
  FILTER LANGMATCHES(LANG(?title), "EN")
  FILTER LANGMATCHES(LANG(?name),  "EN")
}

{
  label @single
  director @single(scope: all) {
    label
  }
  starring(label_en: "Brad Pitt")  @single
}

{
  label @single
  starring(label_en: "Brad Pitt")  @single
}

SELECT DISTINCT ?entity WHERE {
  ?entity a dbpedia-owl:Airport;
          dbpprop:cityServed dbpedia:Italy.
}

SELECT ?name ?deathDate WHERE {
  ?person a dbpedia-owl:Artist;
          rdfs:label ?name;
          dbpedia-owl:birthPlace [ rdfs:label "York"@en ].
  FILTER LANGMATCHES(LANG(?name),  "EN")
  OPTIONAL { ?person dbpprop:dateOfDeath ?deathDate. }
}

CONSTRUCT {
  ?artist a dbpedia-owl:Artist.
  ?artist dbpedia-owl:birthDate ?date.
}
WHERE {
  ?artist dbpedia-owl:influencedBy dbpedia:Pablo_Picasso.
  ?artist a dbpedia-owl:Artist.
  ?artist dbpedia-owl:birthDate ?date.
}

SELECT DISTINCT ?book ?author
WHERE {
  ?book rdf:type dbpedia-owl:Book;
        dbpedia-owl:author ?author.
}
LIMIT 100

SELECT ?award WHERE {
  ?award a dbpedia-owl:Award;
         dbpprop:country [ dbpedia-owl:language dbpedia:Dutch_language ].
}

SELECT DISTINCT ?performer ?name WHERE {
  ?work dbpedia-owl:writer dbpedia:Michael_Jackson;
        dbpedia-owl:musicalArtist ?performer.
  OPTIONAL {
    ?performer rdfs:label ?name.
    FILTER LANGMATCHES(LANG(?name), "EN")
  }
}

{
  label @single
  writer(label_en: "Michael Jackson")  @single
  artist  @single(scope: all) {
    label
  }
}

SELECT ?software ?company WHERE {
  ?software dbpedia-owl:developer ?company.
  ?company dbpedia-owl:locationCountry [ rdfs:label "Belgium"@en ].
}

{
  softwareName: label @single
  developer @single(scope: all) {
    label
    country(label_en: "Belgium")
  }
}

PREFIX yago: <http://dbpedia.org/class/yago/>
SELECT ?person
WHERE {
  ?person a yago:Carpenters, yago:PeopleExecutedByCrucifixion.
}

SELECT ?person
WHERE {
  dbpedia:Brad_Pitt dbpedia-owl:spouse* ?person.
}

PREFIX dbpedia-owl: <http://dbpedia.org/ontology/>
SELECT ?actor ?cause
WHERE {
  ?actor dbpedia-owl:deathCause ?cause.
  ?actor dc:subject <http://dbpedia.org/resource/Category:American_male_film_actors>
}

query @single(scope: all) {
  name(_:"The DBpedia Ontology")
  description
}

SELECT ?dessert ?fruit
WHERE {
  ?dessert dbpedia-owl:type <http://dbpedia.org/resource/Dessert>;
           dbpedia-owl:ingredient ?fruit.
  ?fruit dbpedia-owl:kingdom <http://dbpedia.org/resource/Plant>.
}

SELECT DISTINCT ?device WHERE {
  dbpedia:Raspberry_Pi dbpprop:os ?operatingSystem.
  ?device a dbpedia-owl:Device;
          dbpprop:os ?operatingSystem.
  FILTER (!(?device = dbpedia:Raspberry_Pi))
}

SELECT DISTINCT ?entity ?event
WHERE {
  ?entity a dbpedia-owl:Event;
          rdfs:label ?event;
          ?predicate <http://dbpedia.org/resource/Trentino> .
  FILTER(langMatches(lang(?event), "EN"))
}

SELECT ?titleEng ?title
WHERE {
    ?movie dbpprop:starring [ rdfs:label "Natalie Portman"@en ];
           rdfs:label ?titleEng, ?title.
    FILTER LANGMATCHES(LANG(?titleEng), "EN")
    FILTER (!LANGMATCHES(LANG(?title), "EN"))
}

SELECT ?indDish ?belDish ?ingredient
WHERE {
  ?indDish a dbpedia-owl:Food;
           dbpedia-owl:origin dbpedia:India;
           dbpedia-owl:ingredient ?ingredient.
  ?belDish a dbpedia-owl:Food;
           dbpedia-owl:origin dbpedia:Belgium;
           dbpedia-owl:ingredient ?ingredient.
}

SELECT ?movie
WHERE {
  [ rdfs:label "Brad Pitt"@en ]
    ^dbpedia-owl:starring/dbpedia-owl:director/^dbpedia-owl:director ?movie.
}

PREFIX sdo: <https://schema.org/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

# Select all logtrackitems that are scene description for a program
SELECT DISTINCT ?program ?scene_description
WHERE {
  VALUES ?program {
    <http://data.rdlabs.beeldengeluid.nl/id/program/2101608190180567031>
  }

  ?program rdf:type sdo:CreativeWork .

  ?program sdo:hasPart ?scene_description

} LIMIT 10

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX sdo: <https://schema.org/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

# Personen voor een programma (ook via series/seizoen/scene description)

SELECT ?entity ?entityName where {
  {
    <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> a sdo:CreativeWork ;
      (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer)/
      (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer) ?entity  .

    ?entity skos:prefLabel ?entityName 
  }

  UNION
  {
    <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> sdo:isPartOfSeason ?season .

    ?season  (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer)/
    (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer) ?entity  .

    ?entity skos:prefLabel ?entityName 
  }

  UNION
  {
    <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> sdo:isPartOfSeason/
    sdo:isPartOfSeries ?series . 

    ?series  (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer)/
    (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer) ?entity  .

    ?entity skos:prefLabel ?entityName 
  }

  UNION
  {
    ?scene sdo:hasPart <http://data.rdlabs.beeldengeluid.nl/id/program/2101608160143012931> .

    ?scene  (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer)/
    (sdo:about|sdo:mentions|sdo:creator|sdo:contributor|sdo:actor|sdo:crew|sdo:performer) ?entity  .

    ?entity skos:prefLabel ?entityName 
  }
}

SELECT DISTINCT ?artist ?band ?bandName WHERE {
  { <http://dbpedia.org/resource/Queen_(band)> dbpedia-owl:bandMember ?artist. }
  UNION
  { <http://dbpedia.org/resource/Queen_(band)> dbpedia-owl:formerBandMember ?artist. }
  ?band dbpedia-owl:formerBandMember ?artist;
        rdfs:label ?bandName.
  FILTER (?band != <http://dbpedia.org/resource/Queen_(band)>)
}

SELECT DISTINCT ?person
WHERE {
  dbpedia:Jesus dc:subject ?common.
  ?person a foaf:Person;
          dc:subject ?common.
}
LIMIT 1000

SELECT ?place ?relation
WHERE {
  ?place rdf:type dbpedia-owl:Settlement;
         ?relation dbpedia:Barack_Obama;
}

SELECT ?clubName ?playerName WHERE {
  ?club a dbpedia-owl:SoccerClub;
        dbpedia-owl:ground ?city;
        rdfs:label ?clubName.
  ?player dbpedia-owl:team ?club;
          dbpedia-owl:birthPlace ?city;
          rdfs:label ?playerName.
  ?city dbpedia-owl:country dbpedia:Spain.

  FILTER LANGMATCHES(LANG(?clubName), "EN")
  FILTER LANGMATCHES(LANG(?playerName), "EN")
}

SELECT ?person
WHERE {
  [ rdfs:label "Bruce Willis"@en ]
    (dbpedia-owl:spouse|dbpedia-owl:child) ?person.
}

SELECT ?entity ?label ?comment
WHERE {
  ?entity a dbpedia-owl:MythologicalFigure;
          rdfs:label ?label;
          dc:subject <http://dbpedia.org/resource/Category:Women_in_Greek_mythology>;
          rdfs:comment ?comment

  FILTER(langMatches(lang(?label), "EN"))
  FILTER(langMatches(lang(?comment), "EN"))
}

{
  label
}

